export const { format: formatPrice } = new Intl.NumberFormat('pt-BR', {
  style: 'currency',
  currency: 'BRL',
});

export const { format: formatValue } = new Intl.NumberFormat('pt-BR', {
  style: 'decia',
  currency: 'BRL',
});

export function getCurrencySymbol() {
  return formatPrice(0)
    .replace(/\d/g, '')
    .replace('.', ' ')
    .replace(',', ' ')
    .trim();
}
